import React, { Component } from 'react';
import Typography from '@material-ui/core/Typography';
import {ListGroupItem,ListGroup, CardTitle, CardText, CardActions, Button, Grid, Cell} from 'react-mdl';
import Paper from '@material-ui/core/Paper';


class BookDetails extends Component {

    getNumbers() {
        let myData = this.props.location.aboutProps || {}
        if (myData.book) {
            return Object.keys(myData.book).map(function(item, index){
                     return item
                });
        } else {
            return false;
        }
    }

    render() {

        let myData = this.props.location.aboutProps || {}

        if (myData.book) {
            let imageUrl = 'https://raw.githubusercontent.com/benoitvallon/100-best-books/master/static/' + myData.book.imageLink;
        return(
            <div> 
                    {console.log(myData.book)}
                    <Grid>
                        <Cell></Cell>
                        <Cell style = {{width:'320px'}}>
                            <img src={imageUrl}/>
                        </Cell>
                        <Cell>
                            <Typography variant="h4" gutterBottom>
                                <b>{myData.book.title}</b>       
                            </Typography>
                                <p> by:{myData.book.author}</p>
                        </Cell>
                    </Grid>
                    <Grid xs={12} style={{lineHeight: '24px', fontSize: '24px', textAlign:"center"}}>
                        Description
                    </Grid>
            </div>   
        )}
        else{
            return( 
            <div></div>
            )
        }
    }


}
export default BookDetails;
